import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import CurrencyService from '../../services/currency-service';
import {
	currencyLoaded,
	currencyRequested
} from '../../actions/index';
import Spinner from '../spinner/spinner';

import './home-page.css';

class HomePage extends Component {	
	currencyService = new CurrencyService();

	componentDidMount() {
		this.currencyService
			.getCurrency().then((currency) => {
				this.props.currencyLoaded(currency);
			});


	};
	
	render() {
		const data = this.props.currency;
		const code = this.props.code;
		const loading = this.props.loading;
		const currencyCodeDefault = data.length > 0 ? data[0].key : 0;

		if(loading) {
			return (
				<Spinner />
			);
		}

		const handleChange = (event) => {
			const v = event.target.defaultValue;
			console.log('V: ', v);
		}
 
		return (
			<div className="wrApp">
				<div className="wrCurrencyBlock wrBaseCurrency">
					<div><input onChange={this.handleChange} type="text" className="baseCurrency" defaultValue="1" /></div>
					<span className="currencyCode currencyCode1">USA</span>
				</div>
				<div className="wrCurrencyBlock wrConvertedCurrency">
					<Link to='/currency' className="wrCurrencyCode2">
						<span className="currencyCode currencyCode2">{code}</span>
					</Link>
					<div><input type="text" className="convertedCurrency" /></div>
				</div>
			</div>
		);
	};
};

const mapStateToProps = ({currency, code, value, loading}) => {
	return {
		currency,
		code,
		value,
		loading
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		currencyLoaded: (currency) => {
			dispatch(currencyLoaded(currency));
		},

		currencyRequested: () => {
			dispatch(currencyRequested());
		}
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
