const currencyLoaded = (apiCurrency) => {
	return {
		type: 'CURRENCY_LOADED',
		payload: apiCurrency
	};
};

const convertibleCurrency = (c, v) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code: c,
		value: v
	};
};

export {
	currencyLoaded,
	convertibleCurrency
};
