import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import {
	newVal
} from '../../actions/index';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';

import './home-page.css';

class HomePage extends Component {	


	componentDidMount() {

	};

	handleChange = (e) => {
		this.props.getValue(e.target.value);
	}

	render() {
		const data = this.props.currency;
		const code = this.props.code;
		const value2 = this.props.value2;
		const v3 = this.props.v3;

		console.log('');
		console.log('-->START HOME');
		console.log('Value1: ', this.props.value);
		console.log('Value2: ', this.props.value2);
		console.log('Value3: ', this.props.v3);
		console.log('END HOME<--');
		console.log('');



		return (
			<div className="wrApp">
				<div className="wrCurrencyBlock wrBaseCurrency">
					<div><input onChange={this.handleChange} type="text" className="baseCurrency" defaultValue={value2} /></div>
					<span className="currencyCode currencyCode1">USA</span>
				</div>

				<div className="wrCurrencyBlock wrConvertedCurrency">
					<Link to='/currency' className="wrCurrencyCode2">
						<span className="currencyCode currencyCode2">
							{code}
							<ArrowDropDownIcon />
						</span>
					</Link>
					<div><span className="convertedCurrency">{v3}</span></div>
				</div>

				<div className="circleBlock">
					<ArrowDownwardIcon className="arrowDown" />
				</div>
			</div>
		);
	};
};

const mapStateToProps = ({code, value, value2, v3}) => {
	return {
		code,
		value,
		value2,
		v3
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		getValue: (value) => {
			dispatch(getValue(value));
		}
	};
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
