const currencyLoaded = (apiCurrency) => {
	return {
		type: 'CURRENCY_LOADED',
		payload: apiCurrency
	};
};

const convertibleCurrency = (code, value) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code,
		value
	};
};

const currencyRequested = () => {
	return {
		type: 'FETCH_BOOKS_REQUEST'
	};
};

export {
	currencyLoaded,
	convertibleCurrency,
	currencyRequested
};
