const reducer = (state, action) => {
	if(state === undefined) {
		return {
			currency: [],
			code: 'SELECT CURRENCY',
			value: '',
			value2: 1,
			v3: 0,
			loading: true
		}
	}

	switch(action.type) {
		case 'CURRENCY_LOADED':
			return {
				currency: action.payload,
				code: state.code,
				value: state.value,
				value2: state.value2,
				v3: state.value2*state.value,
				loading: false
			};
		case 'CONVERTIBLE_CURRENCY':
			return {
				currency: state.currency,
				code: action.code,
				value: action.value,
				value2: state.value2,
				v3: state.v3,
				loading: false
			};

		case 'VAL_2':
			return {
				currency: state.currency,
				code: state.code,
				value: state.value,
				value2: action.payload,
				v3: action.payload*state.value,
				loading: false
			};
		default:
			return state;
	}
};

export default reducer;
