const currencyLoaded = (currency) => {
	return {
		type: 'CURRENCY_LOADED',
		payload: currency
	};
};

const convertibleCurrency = (code, value) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code,
		value
	};
}

const newVal = (code, value) => {
	return {
		type: 'VAL_2',
		code,
		value
	};
}

export {
	currencyLoaded,
	convertibleCurrency,
	newVal
};
