const currencyLoaded = (apiCurrency) => {
	return {
		type: 'CURRENCY_LOADED',
		payload: apiCurrency
	};
};

const convertibleCurrency = (code, value) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code,
		value
	};
};

export {
	currencyLoaded,
	convertibleCurrency
};
