import React from 'react';
import {Link} from 'react-router-dom';

import './currency-list-item.css';

const CurrencyListItem = ({data, onGetValue}) => {
	const elements = data.map((item, index) => {
		const {key, value} = item;
		console.log(index);
		//const firstLetter  = data[index].key.charAt(0));
		return (
			<Link key={key} to="/" className="currencyListItem">
				<li onClick={() => onGetValue(key,value)}>{key}</li>
			</Link>
		);
	});
	return (
		<React.Fragment>
			{elements}
		</React.Fragment>
	);
};

export default CurrencyListItem;
