import React from 'react';
import {connect} from 'react-redux';
import CurrencyListItem from '../currency-list-item/currency-list-item';
import {convertibleCurrency} from '../../actions/index';

import './currency-list.css';

const CurrencyList = ({currency, currencyValue, onGetValue}) => {
	return (
		<div className="wrCurrencyList">
			<span className="titleCurrencyList">Currency Codes {currencyValue}</span>
			<ul className="currencyList">
				<CurrencyListItem data={currency} onGetValue={onGetValue} />
			</ul>
		</div>
	);
};

const mapStateToProps = ({currency, currencyValue}) => {
	return {
		currency,
		currencyValue
	};
};

const mapStateDispatchToProps = (dispatch) => {
	return {
		onGetValue: (v) => {
			dispatch(convertibleCurrency(v));
		}
	};
};

export default connect(mapStateToProps, mapStateDispatchToProps)(CurrencyList);
