const initialState = {
	currency: [
		{key: "UA", value: 26}
	]
}

const reducer = (state = initialState, action) => {
	switch(action.type) {
		case 'CURRENCY_LOADED':
			return {
				currency: action.payload
			};
		default:
			return currency;
	}
};

export default reducer;
