import React, {Component} from 'react';
import CurrencyService from '../../services/currency-service';

import './app.css';

export default class App extends Component {

	state = {
		currency: []
	};
	
	currencyService = new CurrencyService();

	componentDidMount() {
		this.loadingCurrency();
	};

	loadingCurrency = () => {
		this.currencyService
			.getCurrency().then((currency) => {
				this.setState({
					currency
				});
			});
	};
	
	render() {
		const {currency} = this.state;
		console.log('API: ', currency);

		return (
			<div className="wrApp">
				<div className="wrCurrencyBlock wrBaseCurrency">
					<div><input type="text" className="baseCurrency" /></div>
					<span className="currencyCode currencyCode1">USA</span>
				</div>
				<div className="wrCurrencyBlock wrConvertedCUrrency">
					<span className="currencyCode currencyCode2">{currency[0].key}</span>
					<div><input type="text" className="convertedCurrency" /></div>
				</div>
			</div>
		);
	}
}
