const initialState = {
	currency: [
		{key: "UA2", value: 26}
	]
}

const reducer = (state = initialState, action) => {
	switch(action.type) {
		case 'CURRENCY_LOADED':
			return {
				currency: action.payload,
				code: state.currency[0].code,
				value: state.currency[0].value,
				loading: false
			};
		case 'CONVERTIBLE_CURRENCY':
			return {
				currency: state.currency,
				code: action.code,
				value: action.value,
				loading: false
			};
		default:
			return state;
	}
};

export default reducer;
