import React from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';

import './currency-list-item.css';

const CurrencyListItem = ({data, onGetValue}) => {
	const elements = data.map((item) => {
		const {key, value} = item;

		return (
			<Link key={key} to="/">
				<li onClick={() => onGetValue(key, value)} className="currencyListItem">{key}</li>
			</Link>
		);
	});
	return (
		<React.Fragment>
			{elements}
		</React.Fragment>
	);
};

const mapStateToProps = ({value2}) => {
	return {
		value2
	};
};

const mapStateDispatchToProps = (dispatch) => {
	return {
		onGetValue: (code, value) => {
			dispatch(convertibleCurrency(code, value, v2));
		}
	};
};

export default connect(mapStateToProps, mapStateDispatchToProps)(CurrencyListItem);
