const currencyLoaded = (currency) => {
	return {
		type: 'FETCH_CURRENCY_SUCCESS',
		payload: currency
	};
};

const currencySelection = (code, value) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code,
		value
	};
}

const newVal = (v) => {
	return {
		type: 'VAL_2',
		payload: v
	};
}

export {
	currencyLoaded,
	currencySelection,
	newVal
};
