export default class currencyService {
	_apiCurrency = 'https://api.privatbank.ua/p24api/exchange_rates?';
	
	getCurrency = async () => {
		const resCurrency = await fetch(`${this._apiCurrency}`);
		
		if(!resCurrency.ok) {
			throw new Error(`Could not fetch ${this._apiCurrency}, received ${resCurrency.status}`)
		}
		return await resCurrency.json();
	}
};
