const reducer = (state, action) => {
	if(state === undefined) {
		return {
			currency: [],
			code: '',
			value: '',
			loading: true
		}
	}

	switch(action.type) {
		case 'CURRENCY_LOADED':
			return {
				currency: action.payload,
				code: state.code,
				value: state.value,
				loading: false
			};
		case 'CONVERTIBLE_CURRENCY':
			return {
				currency: state.currency,
				code: action.code,
				value: action.value,
				loading: false
			};
		case 'FETCH_BOOKS_REQUEST':
			return {
				books: [],
				loading: true,
				error: null
			};
		default:
			return state;
	}
};

export default reducer;
