export default class currencyService {
	_apiCurrency = 'https://api.privatbank.ua/p24api/exchange_rates?json&date=01.12.2014';
	
	getCurrency = async () => {
		const resCurrency = await fetch(`${this._apiCurrency}`, {mode: 'no-cors'});
		
		if(!resCurrency.ok) {
			throw new Error(`Could not fetch ${this._apiCurrency}, received ${resCurrency.status}`)
		}
		return await resCurrency.json().exchangeRate;
	}
};
