const currencyLoaded = (currency, key, value) => {
	return {
		type: 'CURRENCY_LOADED',
		currency,
		key,
		value
	};
};

const convertibleCurrency = (code, value) => {
	return {
		type: 'CONVERTIBLE_CURRENCY',
		code,
		value
	};
};

const currencyRequested = () => {
	return {
		type: 'FETCH_BOOKS_REQUEST'
	};
};

export {
	currencyLoaded,
	convertibleCurrency,
	currencyRequested
};
